import{_ as t,c as e,o as a,ag as o}from"./chunks/framework.vcTIfPnH.js";const m=JSON.parse('{"title":"许久不见的现代诗","description":"","frontmatter":{"layout":"doc","title":"许久不见的现代诗","tags":["记录"]},"headers":[],"relativePath":"Blog/许久不见的现代诗.md","filePath":"Blog/许久不见的现代诗.md","lastUpdated":1740387217000}'),_={name:"Blog/许久不见的现代诗.md"};function r(l,p,s,n,c,d){return a(),e("div",null,p[0]||(p[0]=[o('<h2 id="《盛大出逃》" tabindex="-1">《盛大出逃》 <a class="header-anchor" href="#《盛大出逃》" aria-label="Permalink to &quot;《盛大出逃》&quot;">​</a></h2><p>我走向阳台</p><p>想看看窗外</p><p>忽然听到一声天籁</p><p>是什么</p><p>哦 原来是花鸟树木对我的青睐</p><p>你问它们为何对我偏爱</p><p>原是由于自己心底灵魂的呐喊：</p><p>我想离开</p><p>我想放肆去爱</p><p>我想策划一场盛大出逃</p><p>给自己的青春一个荒诞无稽的交代</p><p>蔷薇编织花冠</p><p>梧桐予我平安</p><p>鸟儿赠我自由自在</p><p>我送我一场盛大出逃</p><p>我为我自己加冕</p><p>然后 我该去向何方？</p><p>何方愿意接纳离经叛道者</p><p>背叛世人的信仰</p><p>去远方 去远方</p><p>漫无目的地流浪</p><p>何处予我心安处</p><p>何处便是吾乡</p><p>我是我自己的主宰</p><p>我有我自己的主张</p><p>去流浪 去流浪</p><p>漫天风雪随我启航</p><p>累了就找个地方天地为床</p><p>感受全天下的爱</p><p>我在肆意翱翔</p><p>反正是一场出逃</p><p>反正总有人变老</p><p>反正还会回到道法的怀抱</p><p>无关紧要</p><p>这样就好</p><p>这样就好</p><p>这样很好</p><p>即得良药</p><blockquote><p>写于2024年5月13日</p></blockquote><p>洗澡时偶有灵感，遂写下这首诗。</p><p>写时想起很多人、很多事，细想起来都是别人的故事。人生的快乐不过就是一场场盛大出逃编织而起的美梦，良药治愈心病，痊愈后自然就梦醒了，回到庸庸碌碌的生活，回到道德与法治的怀抱。</p><p>也可以说是最近有些朋友问的一个问题：演唱会现场的意义是什么？不过是一场盛大的出逃。抛却一切，沉浸其间，那两三个小时的无限快乐。去他的工作，去他的学习，去他的生活，去他的人生。在那一刻，我终于做回了自己。</p><p>作为情绪的载体，这首诗很称职。</p>',44)]))}const u=t(_,[["render",r]]);export{m as __pageData,u as default};
